			function replaceSpecialChars(str) {
    str = str.replace(/[Ã€ÃÃ‚ÃƒÃ„Ã…]/, "A");
    str = str.replace(/[ÃÃ¡Ã¢Ã£Ã¤Ã¥]/, "a");
    str = str.replace(/[ÃˆÃ‰ÃŠÃ‹]/, "E");
    str = str.replace(/[Ã‡]/, "C");
    str = str.replace(/[Ã§]/, "c");

    // o resto

    return str;
}
function strpos (haystack, needle, offset) {
  var i = (haystack+'').indexOf(needle, (offset || 0));
  return i === -1 ? false : true;
}
			function nome_estabelecimento(id){ 
  if (jQuery("#hotel_trend_nh"+id).val().trim().length >= 3){

    if (jQuery("#codigo_trend_n").val() == "" || trim(jQuery("#codigo_trend_n").val()) == "") {

        var destinos_motor = JSON.parse(jQuery("#estabelecimentos_ehtl").val()); 

    }else{
        
        var destinos_motor = JSON.parse(jQuery("#estabelecimentos_total").val()); 

    }

    var dados = '';

    jQuery(destinos_motor).each(function (i, item) { 

            var valor_pesquisado = replaceSpecialChars(jQuery("#hotel_trend_nh"+id).val().toUpperCase());

            if (strpos(replaceSpecialChars(item.nome.toUpperCase()), valor_pesquisado) === true) { 

                dados += '<li class="li_dados" style="list-style: none;border-bottom: 1px solid #ddd;font-size:13px;cursor:pointer;padding: 6px 15px;color: #495057;" onclick="seleciona_hotel(\''+item.nome+'\', \''+item.id+'\', \''+id+'\', \''+item.destination+'\')"><i class="fa fa-bed"></i> '+item.nome+'</li>'; 
            }

    });

    jQuery("#dados").html(dados);
    jQuery("#dados").attr("style", "display:block;position: absolute;width: 93.6%;margin-top: 0px;background-color: #fff;z-index: 999;");

  }
			}

			function seleciona_hotel(destino, id_hotel, id, destination){
  jQuery("#hotel_trend_nh"+id).val(destino); 
  jQuery("#nome_hotel_trend_nh"+id).val(destino); 
  jQuery("#id_hotel_trend_nh"+id).val(id_hotel); 
  jQuery("#destination_hotel_trend_nh"+id).val(destination); 
      jQuery("#dados").attr("style", "display:none;position: absolute;width: 30.9%;margin-top: 0px;background-color: #fff;z-index: 999;");
}


		</script>

